var adjustSensor = function(val, wasIncreasing) {
    var delta = Math.random();
    var newVal = 0;
    var newIsIncreasing = true;
    if (!val) {
        newVal = delta;
        newIsIncreasing = true;
    } else {
        newIsIncreasing = (wasIncreasing) ? ((val + delta) < 10) : ((val - delta) < 0);
        newVal = (newIsIncreasing) ? val + delta : val - delta;
    }
    return { value: newVal, isIncreasing: newIsIncreasing }
}

