var mapToRange = function(num, out_min, out_max) { // from stackoverflow
  var in_min = 0;
  var in_max = 255;
  return Math.floor((num - in_min) * (out_max - out_min) / (in_max - in_min) + out_min);
};

var dec2Hex = function(d, padding) { // from stackoverflow decimalToHex
    var hex = Number(d).toString(16);
    padding = typeof (padding) === "undefined" || padding === null ? padding = 2 : padding;
    while (hex.length < padding) {
        hex = "0" + hex;
    }
    return hex;
};

/**
 * Returns a rgb hex representation of the intensity of a cold white led.
 * Performs a linear mapping of the range 0-255 to 3 different ranges (one for each component red, green, blue)
 */
var intensity2Rgb_coldWhite = function(intensity) {
    var red = mapToRange(intensity, 0, 220);
    var green = mapToRange(intensity, 0, 220);
    var blue = mapToRange(intensity, 0, 255);
    var ret = "#" + dec2Hex(red, 2) + dec2Hex(green, 2) + dec2Hex(blue, 2);
    // node.warn("cold white : " + ret);
    return ret;
};

var intensity2Rgb_warmWhite = function(intensity) {
    var red = mapToRange(intensity, 0, 255);
    var green = mapToRange(intensity, 0, 255);
    var blue = mapToRange(intensity, 0, 200);
    var ret = "#" + dec2Hex(red, 2) + dec2Hex(green, 2) + dec2Hex(blue, 2);
    // node.warn("warm white : " + ret);
    return ret;
};

var intensity2Rgb_amber = function(intensity) {
    var red = mapToRange(intensity, 0, 255);
    var green = mapToRange(intensity, 0, 100);
    var blue = mapToRange(intensity, 0, 100);
    var ret = "#" + dec2Hex(red, 2) + dec2Hex(green, 2) + dec2Hex(blue, 2);
    // node.warn("amber : " + ret);
    return ret;
};

global.set('f_intensity2Rgb_coldWhite', intensity2Rgb_coldWhite);
global.set('f_intensity2Rgb_warmWhite', intensity2Rgb_warmWhite);
global.set('f_intensity2Rgb_amber', intensity2Rgb_amber);

