/**
 * Sets up the base intensity for the leds.
 * Also initializes the multiplying factors that will be used in the following nodes.
 * Sends a message only if the input msg satisfies the following condition : msg.payload === 'refresh' to avoid refreshing when the sliders are moved.
 */

var stripLength = global.get('stripLength');
var orientations = global.get('orientations');

// Getting the slider values when they are changed...
if (msg.topic === 'slider_coldWhite') { // TODO define the test more precisely, not c style...
    context.set('coldWhite', msg.payload);
}
