    context.set('warmWhite', msg.payload);
}
if (msg.topic === 'slider_amber') { // TODO define the test more precisely, not c style...
    context.set('amber', msg.payload);
}

// the LED refresh loop
if (msg.payload === 'refresh') {
    var initLEDs = global.get('f_initLEDs');
    initLEDs(msg);

    var coldI = context.get('coldWhite');
    var warmI = context.get('warmWhite');
    var amberI = context.get('amber');

    var orientation;
    for (var j = 0; j < orientations.length; j++) {
        orientation = orientations[j];
        for (i = 0; i < stripLength; i++) {
            msg.led.tree[orientation][i] = [coldI, warmI, amberI];
            msg.factors.tree[orientation][i] = [1.0, 1.0, 1.0];
        }
    }
}

// node.warn("IN - msg.factors.tree['north1'][0] : " + msg.factors.tree['north1'][0]);

return msg;

if (global.get('effect_pulse')) {
    var stripLength = global.get('stripLength');
    var orientations = global.get('orientations');

    var step = 0.1;

    var isUp = context.get('isUp')||false; // if the intensity is going up
    var factor = context.get('factor')||1.0; // the current factor

    isUp = (isUp) ? (factor + step) < 1.0 : (factor - step) < 0.0;
    factor = (isUp) ? factor + step : factor - step;
    context.set('isUp', isUp);
    context.set('factor', factor);

    var orientation;
    for (var j = 0; j < orientations.length; j++) {
        orientation = orientations[j];
        for (var i = 0; i < stripLength; i++) {
            msg.factors.tree[orientation][i] = 
                [factor * msg.factors.tree[orientation][i][0], 
                factor * msg.factors.tree[orientation][i][1], 
                factor * msg.factors.tree[orientation][i][2]];
        }
    }
}

