/**
 * Creates a sort of histogram effect.
 */
if (global.get('effect_histogram')) {

    var stripLength = global.get('stripLength');
    var orientations = global.get('orientations');

    var maxes = context.get('maxes')||[];
    var isUp = context.get('isUp')||[];
    var newIsUp = [];
    // initializing the maxes
    if (maxes['north1'] === undefined) { // test maxes.length does not work
        // node.warn('maxes[north1] is undefined');
        for (var orientation in msg.led.tree) {
            if (msg.led.tree.hasOwnProperty(orientation)) {
                isUp[orientation] = true;
                maxes[orientation] = 0;
            }
        }
    }

    var factor = 0.0;
    var orientation;
    for (var j = 0; j < orientations.length; j++) {
        orientation = orientations[j];
        newIsUp[orientation] = isUp[orientation] ? (Math.random() > 0.1) : (Math.random() > 0.9);
        delta = (newIsUp[orientation]) ? 1 : -1;
        // node.warn('max before : ' + maxes[orientation]);
        maxes[orientation] = (delta > 0) ? Math.min(stripLength - 1, maxes[orientation] + delta) : Math.max(0, maxes[orientation] + delta);
        // node.warn('max : ' + maxes[orientation] + ' ; delta : ' + delta);
        for (var i = 0; i < stripLength; i++) {
            factor = (i < maxes[orientation]) ? 1.0 : 0.0;
            msg.factors.tree[orientation][i] = 
                [factor * msg.factors.tree[orientation][i][0], 
                factor * msg.factors.tree[orientation][i][1], 
                factor * msg.factors.tree[orientation][i][2]];
        }
    }
    context.set('isUp', newIsUp);
    context.set('maxes', maxes);
}

// node.warn("Histogram - msg.factors.tree['north1'][0] : " + msg.factors.tree['north1'][0]);

