        msg.led.tree[orientation][i][1] = Math.floor(msg.factors.tree[orientation][i][1] * msg.led.tree[orientation][i][1]);
        msg.led.tree[orientation][i][2] = Math.floor(msg.factors.tree[orientation][i][2] * msg.led.tree[orientation][i][2]);
        coldRgb = intensity2Rgb_coldWhite(msg.led.tree[orientation][i][0]);
        warmRgb = intensity2Rgb_warmWhite(msg.led.tree[orientation][i][1]);
        amberRgb = intensity2Rgb_amber(msg.led.tree[orientation][i][2]);
        msg.led_rgb.tree[orientation][stripLength - i - 1] = [coldRgb, warmRgb, amberRgb]; // inversion for display
    }
}

return msg;

/**
