 */

if (msg.payload === 'flow_start') { // if this is the workflow startup, defines the default values
    msg.payload = 'action_black';
}

return msg;

if (global.get('effect_updown')) {
    var stripLength = global.get('stripLength');
    var orientations = global.get('orientations');

    var tmpFactors = [0.0, 0.0, 0.0];
    var orientation;
    for (var j = 0; j < orientations.length; j++) {
        orientation = orientations[j];
        for (var i = 0; i < (stripLength / 2); i++) {
            tmpFactors = msg.factors.tree[orientation][i];
            msg.factors.tree[orientation][i] = msg.factors.tree[orientation][stripLength - i - 1];
            msg.factors.tree[orientation][stripLength - i - 1] = tmpFactors;
        }
    }
}

